import React, { Component } from 'react'
import Formulario from './components/Formulario';
import ListaDeNotas  from './components/ListaDeNotas';
import ListaDeCategorias from './components/ListaDeCategorias';
import "./assets/app.css"
import "./assets/index.css"

class App extends Component {

  constructor(){
    super();

    this.state = {
      notas:[],
      categorias:["Trabalho", "Esporte"],
    };
  }

  criarNota(titulo, texto){
    const novaNota =  {titulo, texto};
    const novoArayNotas = [...this.state.notas, novaNota]
    const novoEstado = {
      notas:novoArayNotas
    }

    this.setState(novoEstado)
  }

  adicionarCategoria(nomeCategoria){
    const novoArrayDeCategoria = [...this.state.categorias, nomeCategoria];
    const novoEstado = {...this.state, categorias:novoArrayDeCategoria};
    this.setState(novoEstado)
  }

  deletarNota(index){
    let arrayNotas = this.state.notas;
    arrayNotas.splice(index,1);
    this.setState({notas:arrayNotas})
  }

  render(){
    return (
      <section className="conteudo" >
        <Formulario criarNota={this.criarNota.bind(this)}/>
        <main className="conteudo-principal">
          <ListaDeCategorias
            adicionarCategoria={this.adicionarCategoria.bind(this)}
            categorias={this.state.categorias}
          />
          <ListaDeNotas 
            apagarNota={this.deletarNota.bind(this)}
            notas={this.state.notas}
          />
        </main>
      </section>
    );
  }
}

export default App;
